typedef struct node{
	int data;
	struct node *next;
}node;

node *creat(int n){
	int i;
	node *p=NULL,*head,*a;
	head = (node*)malloc(sizeof(node));
	p=head;
	if(0!=n){
		for(i=1;i&lt;=n;){
			a=(node*)malloc(sizeof(node));
			a-&gt;data=i++;
			p-&gt;next=a;
			p=a;
		}
		a-&gt;next=head-&gt;next;

	}
	free(head);
	return a-&gt;next;
}

